$:/plugins/ajh/tiddlytime/datetime.js.text:
/*\
title: $:/plugins/ajh/tiddlytime/datetime.js
type: application/javascript
module-type: macro

<<dt format delay>>

Examples:
<<dt>>
<<dt "YYYY-0MM-0DD 0hh:0mm:0ss">>
<<dt "YYYY-0MM-0DD 0hh:0mm:0ss" "30">>
<$macrocall $name="dt" ts={{!!created}} format="MMM DD. YY"/>

Format Strings:
Please find all format strings listed in the JavaScript code of function formatDateString in $:/core/modules/utils/utils.js
\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

/*
This is a macro to get formatted date and/or time plus delay in minutes
*/

exports.name = "dt";

exports.params = [
	{ name: "format" },
	{ name: "delay" },
	{ name: "ts" }
];

/*
Run the macro
*/
exports.run = function(format, delay, ts) {
	if(!ts) {
		ts = new Date;
		ts.setMinutes(ts.getMinutes() + Number(delay));
	}
	else {
		ts = $tw.utils.parseDate(ts);
	}
	if(!format)
		format = "hh12:0mm:0ss AM";
	return $tw.utils.formatDateString(ts, format); 
};

})();

