text:
/** 顶部tab栏 */
section.story-backdrop {
  position: fixed;
  top: 0;
  z-index: 1000;
  width: 100vw;
  overflow-x: auto;
  left: 0;
  white-space: nowrap;
  overflow: -moz-scrollbars-none;
  scrollbar-width: none;
}
section.story-backdrop::-webkit-scrollbar {
  display: none;
}
/** tiddlers bar #eee8d5*/
section.story-backdrop > p, section.story-backdrop > p > div.tc-tab-buttons {
  width: 100%;
  margin: 0;
  display: flex;
  background-color: none;
  backdrop-filter: blur(10px);
  background: <<colour button-background>>;
  /** make it possible to drag the app using this bar in TiddlyGit */
  -webkit-app-region: drag;
}


section.story-backdrop button {
  border: none;
  border-radius: 0;
  margin: 0;
  padding: 5px 10px;
  padding-right: 2px;
}
/** x button */
section.story-backdrop button:not(.tc-tab) {
  border: none;
  border-radius: 0;
  margin: 0;
  padding: 0 5px;
  opacity: 0;
  background-color: none;
}
section.story-backdrop button:not(.tc-tab):hover {
  opacity: 1;
}
section.story-backdrop button.tc-tab:not(.tc-tab-selected):hover {
  border-bottom: 2px solid gray;
}
section.story-backdrop button.tc-tab:nth-child(2) {
  margin-left: -1em;
}
.tc-tab-buttons {
  padding: 0;
  margin: 0;
}

div.tc-page-container {
  margin-top: 30px;
}

div.tc-tiddler-title {
  top: 24px;
}
div.tc-editor-toolbar {
  top: calc(35px + 24px);
}
/** 收起侧边栏的按钮 */
.tc-topbar-right {
  top: 18px;
}
