<style>
.bp-table {margin: 0 auto; }
.bp-table td {
  vertical-align:top;
 }
.bp-margin {
  margin-left:2em;
}
span.bp-decode > code {
  display:inline;
  font-style:normal;
  background:white;
  border:0;
  color:#5778d8; 
  margin-right:-5px;
  margin-left:-5px;
}
.bp-smaller {
  font-size:80%;
  margin-left:1em;
  line-height:140%;
}
</style>

$:/plugins/HackSearch/Stylesheet.text:
/* $:/plugins/HackSearch/Buttons/ItemTemplate */
.hacksearch-icon svg {
  max-width:1em;
  max-height:1em;
  vertical-align:middle;
  margin:0 4px;
}


.hacksearch-icontemplate {
  display:inline;
  padding:0;
  margin:0;
}
.hacksearch-icontemplate svg {
  fill:orange;
  height:.8em;
  width:.8em;
  vertical-align:middle;
}
