$:/plugins/telmiger/Btx/macros.text:
\define TiddlerExpress(step:"0")
<$let
	nth={{{ [list[$:/StoryList]allbefore:include<currentTiddler>count[]add[$step$]] }}}
	target={{{ [list[$:/StoryList]nth<nth>] }}}
	focus={{{ section.lox-tiddler:nth-child([<nth>] n+1) button :and[join[]] }}}
	refocus={{{ section.lox-tiddler:nth-child([<nth>] n+1) a :and[join[]] }}}
>
<$action-sendmessage $message="tm-focus-selector" $param=<<focus>> preventScroll="true"/>
<$action-sendmessage $message="tm-focus-selector" $param=<<refocus>> preventScroll="true"/>
<$action-navigate $to=<<target>>/>
</$let>
\end

$:/plugins/telmiger/Btx/styles/buttons.c.text:
.lox-tiddler-toolbar span {
  line-height: 0;
}

.lox-tiddler-bottom .lox-tiddler-toolbar > button, 
.lox-tiddler-bottom .lox-tiddler-toolbar > span > button,
.lox-tiddler-bottom .lox-tiddler-toolbar > span > span > button {
   margin-bottom: -0.75em;
   padding-left: 0.07em;
   background: <<colour tiddler-background>>;
   border: 1px solid <<colour tiddler-border>>;
   border-radius: 1em;
   height: 1.5em;
   width: 1.5em;
   font-size: 1em;
   line-height: 0;
}

.lox-tiddler-bottom .lox-tiddler-toolbar > button:hover, 
.lox-tiddler-bottom .lox-tiddler-toolbar > span > button:hover,
.lox-tiddler-bottom .lox-tiddler-toolbar > span > span > button:hover {
   background: <<colour tiddler-background>>;
}

.lox-tiddler-bottom .lox-tiddler-toolbar > button .tc-btn-text, 
.lox-tiddler-bottom .lox-tiddler-toolbar > span > button .tc-btn-text,
.lox-tiddler-bottom .lox-tiddler-toolbar > span > span > button .tc-btn-text {
   position: absolute;
   margin-top: -0.5em;
   margin-left: 0.5rem;
   font-size: 0.5em;
   line-height: 0.8;
}

.lox-tiddler-bottom .lox-tiddler-toolbar button .tc-btn-text:first-child {
   margin-top: -1.25em;
   margin-left: 1.75em;
}

$:/plugins/telmiger/Btx/styles/style.css.text:
.lox-tiddler-bottom {
   position: relative;
   display: flex;
   flex-direction: row;
   flex-wrap: wrap;
   justify-content: space-between;
   align-items: baseline;
   padding-bottom: 0.25em;
}

.lox-tiddler-bottom .lox-tiddler-toolbar {
   position: relative;
   align-content: center;
   display: flex;
   justify-content: space-evenly;
   width: 100%;
   margin: 0 calc(35% - 4em) 0 calc(35% - 4em);
   padding-top: 1.125rem;
   border: 1px solid transparent;
   border-bottom: 1px solid <<colour tiddler-border>>;
   font-size: 1.5em;
}


 /* enlarge bottom toolbar spacing if top is invisible and tiddler spacing is off */

<$list filter="""[all[shadows+tiddlers]suffix[styles/hide/top.css]tag[$:/tags/Stylesheet]]""">
<$list filter="""[all[shadows+tiddlers]suffix[styles/tiddler-spacing.css]!tag[$:/tags/Stylesheet]]""">

.lox-tiddler-bottom .lox-tiddler-toolbar {
   margin-bottom: 2rem;
}

</$list>
</$list>


 /* Left border animation – for Lox tiddlers only */

.lox-tiddler .lox-tiddler-bottom .lox-tiddler-toolbar::before {
   content: "";
   position: absolute;
   bottom: 0;
   left: -1px;
   height: 0%;
   width: 1px;
} 

.lox-tiddler .lox-tiddler-bottom .lox-tiddler-toolbar:hover::before {
   height: 100%;
   border-left: 1px solid <<colour tab-divider>>;
   border-left: 1px solid <<colour tiddler-border>>;
   transition: border-color 0s, height cubic-bezier(.08,.82,.17,1) {{$:/config/AnimationDuration}}ms;
} 


/* readme / config */

.tc-plugin-info-dropdown legend {
   padding: 0 0.5em;
}

.tc-plugin-info-dropdown fieldset {
   margin-bottom: 1rem;
}
